f = open('rosalind_hamm.txt', 'r')
raw = f.readlines()
f.close()

for idx, line in enumerate(raw):
    raw[idx] = line.strip()

# s = 'GAGCCTACTAACGGGAT'
# t = 'CATCGTAATGACGGCCT'

s = raw[0]
t = raw[1]

# s = ''
# t = ''

def hamming(a, b):
    ctr = 0
    for idx in range(len(a)):
        if a[idx] != b[idx]:
            ctr += 1
    return ctr

print(hamming(s, t))
