def process_line(txt, codondict):
    txt = txt.strip()
    txtlist = txt.split('    ')
    txtlist = txt.split('   ')
    while '' in txtlist:
        txtlist.remove('')
    tmpdict = {}
    for item in txtlist:
        tmplist = item.split(' ')
        codondict[tmplist[0]] = tmplist[1]
    return

def find_start(s):
    flag = False
    i, j, k = 0, len(s), None
    while flag == False:
        if s[i:(i+3)] == 'AUG':
            k = i
            flag = True
        elif i == (j-2):
            flag = True
        else:
            i += 1
    if k is not None:
        return s[k:]
    else:
        return s

def translate(s, codondict):
    flag = False
    i, j, k = 0, len(s), None
    protein = ''
    while flag == False:
        cdn = s[i:(i+3)]
        amino = codondict[cdn]
        if amino == 'Stop' or i > (j-2):
            flag = True
        else:
            protein += amino
            i += 3
    return protein


f = open('rna_codon.txt', 'r')
codons = {}
for line in f:
    process_line(line, codons)
f.close()

rnastring = ''
rnastring = find_start(rnastring)
protstring = translate(rnastring, codons)

print(protstring)
